class Solution {
public:
    #define pii pair<int, int>
    #define fi first
    #define se second

    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = 0;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                int p = n - i - 1, q = m - j - 1;
                if (i == p || j == q) continue;
                int cnt = a[i][j] + a[p][j] + a[i][q] + a[p][q];
                if (cnt == 0 || cnt == 4) continue;
                if (cnt == 3) {
                    a[i][j] = a[p][j] = a[i][q] = a[p][q] = 1;
                    ++ans;
                }
                else {
                    a[i][j] = a[p][j] = a[i][q] = a[p][q] = 0;
                    ans += cnt;
                }
            }
        }


        if (n % 2 == 0 && m % 2 == 0) return ans;

        vector<pii> v;
        v.emplace_back(0, 0);
        if (n % 2 == 1 && m % 2 == 1)
            if (a[n / 2][m / 2])
                ++ans;
        if (n % 2 == 1)
            for (int j = 0; j < m; ++j) {
                int q = m - j - 1;
                if (q <= j) break;
                v.emplace_back(a[n / 2][j], a[n / 2][q]);
            }
        if (m % 2 == 1)
            for (int i = 0; i < n; ++i) {
                int p = n - i - 1;
                if (p <= i) break;
                v.emplace_back(a[i][m / 2], a[p][m / 2]);
            }

        // cout << ans << '\n';
        // for (auto [a, b] : v) cout << a << ' ' << b << '\n';

        int t = v.size() - 1;
        vector<vector<int>> f(t + 1, vector<int>(2, 1e9));
        f[0][0] = 0;
        for (int i = 1; i <= t; ++i) {
            int w0 = v[i].fi + v[i].se, w1 = 2 - w0;
            f[i][0] = min(f[i - 1][0] + w0, f[i - 1][1] + w1);
            f[i][1] = min(f[i - 1][0] + w1, f[i - 1][1] + w0);
        }

        ans += f[t][0];
        return ans;
    }
};
