class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        int res = 0;
        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                int sum = grid[i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][j] + grid[m - 1 - i][n - 1 - j];
                res += Math.min(sum, 4 - sum);
            }
        }
        int res1 = 0;
        int cnt = 0;
        if (m % 2 != 0) {
            for (int j = 0; j < n / 2; j++) {
                if (grid[m / 2][j] != grid[m / 2][n - 1 - j]) {
                    res1++;
                } else {
                    cnt += grid[m / 2][j] * 2;
                }
            }
        }
        if (n % 2 != 0) {
            for (int i = 0; i < m / 2; i++) {
                if (grid[i][n / 2] != grid[m - 1 - i][n / 2]) {
                    res1++;
                } else {
                    cnt += grid[i][n / 2] * 2;
                }
            }
        }
        if (cnt % 4 != 0 && res1 == 0) {
            res1 += 2;
        }
        res += res1;
        if (n % 2 != 0 && m % 2 != 0 && grid[m / 2][n / 2] != 0) {
            res++;
        }
        return res;
    }
}
