class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size() ;
        int m = grid[0].size() ;

        int ans = 0 ;

        for(int i=0 ; i<n/2 ; i++){
            for(int j=0 ; j<m/2 ; j++){
                int a = grid[i][j] ;
                int b = grid[i][m-1-j] ;
                int c = grid[n-1-i][j] ;
                int d = grid[n-1-i][m-1-j] ;

                int count = 0 ;

                if(a==1) count++ ;
                if(b==1) count++ ;
                if(c==1) count++ ;
                if(d==1) count++ ;

                ans += min(count, 4-count) ; 
            }
        }

        if( n%2 && m%2 && grid[n/2][m/2] ) ans++ ;

        int count1 = 0 , count2 = 0 ;
        int free = 0 ;

        if(n%2){
            for(int j=0 ; j<m/2 ; j++){
                int a = grid[n/2][j] ;
                int b = grid[n/2][m-1-j] ;

                if(a!=b) free++ ;
                if(a==1 || b==1) count1++ ;

            }
        } 

        if(m%2){
            for(int i=0 ; i<n/2 ; i++){
                int a = grid[i][m/2] ;
                int b = grid[n-1-i][m/2] ;

                if(a!=b) free++ ;
                if(a==1 || b==1) count2++ ;

            }
        } 

        ans += free ;

        if(free==0)
            if( (count1 + count2) % 2 ) ans += 2 ;

        return ans ;

    }
};
