class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n=len(grid),len(grid[0])
        ans=0
        for i in range(m//2):
            for j in range(n//2):
                cnt=[0,0]
                for x,y in (i,j),(i,n-1-j),(m-1-i,j),(m-1-i,n-1-j):
                    cnt[grid[x][y]]+=1
                ans+=min(cnt)
        print(f"ans={ans}")
        if m&1 and n&1:
            ans+=grid[m//2][n//2]!=0
        diff=0
        same=0
        if m&1:
            for j in range(n//2):
                diff+=grid[m//2][j]!=grid[m//2][n-1-j]
                if grid[m//2][j]==grid[m//2][n-1-j]==1:
                    same+=1
            print(diff)
        if n&1:
            for i in range(m//2):
                diff+=grid[i][n//2]!=grid[m-1-i][n//2]
                if grid[i][n//2]==grid[m-1-i][n//2]==1:
                    same+=1
            print(diff)
        ans+=diff
        if diff==0 and (same*2)%4==2:
            ans+=2
        return ans
