class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                vector<int> v(2,0);
                v[grid[i][j]]++;
                v[grid[i][m-j-1]]++;
                v[grid[n-i-1][j]]++;
                v[grid[n-i-1][m-j-1]]++;
                ans += min(v[0],v[1]);
            }
        }
        int total = 0;
        int change = 0;
        if(n&1){
            int i=0,j=m-1;

            while(i<j){
                if(grid[n/2][i] != grid[n/2][j]){
                    ans++;
                    change++;
                }else{
                    if(grid[n/2][i] == 1){
                        total+=2;
                    }
                }
                i++;
                j--;

            }
        }
        if(m&1){
            int i=0,j = n-1;
            while(i<j){
                if(grid[i][m/2] != grid[j][m/2]){
                    ans++;
                    change++;
                }else{
                    if(grid[i][m/2] == 1){
                        total+=2;
                    }
                }

                i++;
            j--;
            }
        }
        int final = 0;
        if((n&1) && (m&1)){
            if(grid[n/2][m/2] == 1)final++;
        }
        if(total%4 != 0){
            if(change == 0){
                final += 2;
            }
        }
        return ans+final;
    }
};
