class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        long long cnt = 0, mov = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == 1) {
                    if (i != n - i - 1 && j != m - j - 1) {
                        int tmp = grid[i][j] + grid[i][m - j - 1] +
                                  grid[n - i - 1][j] +
                                  grid[n - i - 1][m - j - 1];
                        if (tmp == 1)
                            ans += 1;
                        else if (tmp == 2)
                            ans += 2;
                        else if (tmp == 3)
                            ans += 1;
                        grid[i][j] = 0;
                        grid[i][m - j - 1] = 0;
                        grid[n - i - 1][j] = 0;
                        grid[n - i - 1][m - j - 1] = 0;
                    } else if (i != n - i - 1) {
                        int tmp = grid[i][j] + grid[n - i - 1][j];
                        if (tmp == 1)
                            mov++;
                        cnt += tmp + mov;
                        grid[i][j] = 0;
                        grid[i][m - j - 1] = 0;
                        grid[n - i - 1][j] = 0;
                        grid[n - i - 1][m - j - 1] = 0;
                    } else if (j != m - j - 1) {
                        int tmp = grid[i][j] + grid[i][m - j - 1];
                        if (tmp == 1)
                            mov++;
                        cnt += tmp + mov;
                        grid[i][j] = 0;
                        grid[i][m - j - 1] = 0;
                        grid[n - i - 1][j] = 0;
                        grid[n - i - 1][m - j - 1] = 0;
                    } else {
                        if (grid[i][j] == 1)
                            ans++;
                    }
                }
            }
        }
        ans += mov;
        if (cnt % 4 != 0) {
            if(mov == 0) ans += 2;
        }
        return ans;
    }
};
