class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(), m=grid[0].size();
        int ans=0, singleo=0, due=0, deo=0;
        for (int i=0; i<(n+1)/2; i++){
            for (int j=0; j<(m+1)/2; j++){
                int cnt[2]={};
                cnt[grid[i][j]]++;
                cnt[grid[i][m-j-1]]++;
                cnt[grid[n-i-1][j]]++;
                cnt[grid[n-i-1][m-j-1]]++;
                if (i==n-i-1 && j==m-j-1){
                    if (grid[i][j]==1)singleo++;
                    continue;
                }else if (i==n-i-1){
                    if (cnt[0]==cnt[1]){
                        ans++;
                        due++;
                    }else{
                        if (grid[i][j]==1)deo++;
                        // cout<<"i "<<cnt[0]<<" "<<cnt[1]<<endl;
                    }
                }else if (j==m-j-1){
                    if (cnt[0]==cnt[1]){
                        ans++;
                        due++;
                    }else {
                        if (grid[i][j]==1)deo++;

                        // cout<<"j "<<cnt[0]<<" "<<cnt[1]<<endl;
                    }
                }
                else {
                    if (cnt[0]==cnt[1])ans+=2;
                    else if (cnt[0]==0 || cnt[1]==0)continue;
                    else ans++;
                }
            }
        }
        // cout<<deo<<" "<<due<<" "<<singleo<<endl;
        if (deo%2==1 && due==0)ans+=2;
        if (singleo)ans++;
        return ans;
    }
};
