class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = 0 ;
        for(int i = 0 ; i < n / 2; i ++){
            for(int j = 0 ; j < m / 2; j ++){
                int o = 0  ;
                o += a[i][j] == 1; 
                o += a[n - i - 1][j] == 1 ;
                o += a[i][m - j - 1] == 1 ;
                o += a[n - i - 1][m - j - 1] == 1 ;

                ans += min(o, 4 - o);
            }
        }

        if((n & 1) and (m & 1)){
            ans += a[n/2][m/2] == 1 ;
        }

        int cnt1 = 0, cnt2 = 0 ;
        if(m & 1){
            for(int i = 0 ; i < n/ 2; i ++){
                if(a[i][m / 2] == 0 and a[n - i - 1][m / 2] == 0){
                    continue;
                }
                if(a[i][m/2] == a[n - i - 1][m / 2]) cnt2 ++ ;
                else cnt1 ++ ;
            }
        }

        if(n & 1){
            for(int j = 0 ; j < m / 2 ; j ++){
                if(a[n / 2][j] == a[n / 2][m - j - 1]){
                    cnt2 += a[n / 2][j] == 1 ;
                }else cnt1 ++ ;
            }
        }

        ans += cnt1 ;
        if(cnt2 & 1){
            if(cnt1 < 1){
                ans += 2 ;
            }
        }

        return ans;
    }
};
