class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<int> f(4, 1000000);
        vector<int> g(4, 1000000);
        f[0] = 0;
        int n = grid.size();
        int m = grid[0].size();
        vector<vector<int>> vis(n);
        for(int i = 0; i < n; i++){
            vis[i].resize(m, 0);
        }
        for(int i = 0; i < n/2 + 1; i++){
            for(int j = 0; j < m/2 + 1; j++){
                if(vis[i][j]) continue;
                g = f;
                f[0] = 1000000;
                f[1] = 1000000;
                f[2] = 1000000;
                f[3] = 1000000;
                int cnt[2] = {0};
                cnt[grid[i][j]]++;
                vis[i][j] = 1;
                if(vis[i][m-j-1] == 0) cnt[grid[i][m-j-1]]++, vis[i][m-j-1] = 1;
                if(vis[n-1-i][j] == 0) cnt[grid[n-1-i][j]]++, vis[n-1-i][j] = 1;
                if(vis[n-1-i][m-1-j] == 0) cnt[grid[n-1-i][m-1-j]]++, vis[n-1-i][m-1-j] = 1;
                // cout << i << " " << j << " " << cnt[0] << " " << cnt[1] << endl;
                int tot = cnt[0] + cnt[1];
                for(int k = 0; k < 3; k++){
                    f[(k+tot)%4] = min(f[(k+tot)%4], g[k] + cnt[0]);
                    f[k] = min(f[k], g[k] + cnt[1]);
                }
                // cout << f[0] << endl;
                //  cout << f[1] << endl;
                //  cout << f[2] << endl;
                //  cout << f[3] << endl;
            }
        }
        return f[0];
    }
};
