function minFlips(a: number[][]): number {
  const m = a.length;
  const n = a[0].length;

  const max = 1000000000;
  var dp: number[] = new Array(4).fill(max);

  var c1 = 0;
  for (var i = 0; i < m; i++) {
    for (var j = 0; j < n; j++) {
      if (a[i][j] === 1) {
        c1++;
      }
    }
  }
  dp[c1 % 4] = 0;

  const m1 = Math.floor((m + 1) / 2);
  const n1 = Math.floor((n + 1) / 2);

  debug && console.log(m, n, m1, n1);
  for (var i = 0; i < m1; i++) {
    for (var j = 0; j < n1; j++) {
      var dp2: number[] = new Array(4).fill(max);
      var c1 = 0;
      var cc = 1;
      if (a[i][j] === 1) {
        c1++;
      }
      if (j !== n - 1 - j) {
        if (a[i][n - 1 - j] === 1) {
          c1++;
        }
        cc++;
      }
      if (i !== m - 1 - i) {
        if (a[m - 1 - i][j] === 1) {
          c1++;
        }
        cc++;
      }
      if (i !== m - 1 - i && j !== n - 1 - j) {
        if (a[m - 1 - i][n - 1 - j] === 1) {
          c1++;
        }
        cc++;
      }
      debug && console.log("Check : ", i, j, cc, c1);

      var c0 = cc - c1;
      for (var t1 = 0; t1 < 4; t1++) {
        // 0 -> 1
        var t2 = (t1 + c0) % 4;
        dp2[t2] = Math.min(dp2[t2], dp[t1] + c0);

        // 1 -> 0
        t2 = (t1 - c1 + 4) % 4;
        dp2[t2] = Math.min(dp2[t2], dp[t1] + c1);
      }
      dp = [...dp2];
      debug && console.log("after:", i, j, dp);
    }
  }
  return dp[0];
}

const debug = false;
