class Solution {
fun minFlips(grid: Array<IntArray>): Int {
    val m = grid.size
    val n = grid[0].size

    var ans = 0
    for (i in 0 until m / 2) {

        for (j in 0 until n / 2) {
            val a = grid[i][j]
            val b = grid[i][n - 1 - j]
            val c = grid[m - 1 - i][j]
            val d = grid[m - 1 - i][n - 1 - j]

            val arr = intArrayOf(a, b, c, d)
            arr.sort()

            val mid = arr[1]
            ans += arr.count { it != mid }
        }
    }
    if (m % 2 == 1 && n % 2 == 1) {
        if (grid[m/2][n/2] == 1) {
            grid[m/2][n/2] = 0
            ans++
        }
    }

    var changeCnt = 0
    var oneCnt = 0

    if (m % 2 == 1) {
        for (j in 0 until n / 2) {
            val a = grid[m/2][j]
            val b = grid[m/2][n - 1 - j]

            if (a != b) {
                changeCnt++
            } else if (a == 1) oneCnt += 2
        }
    }

    if (n % 2 == 1) {
        for (i in 0 until m / 2) {
            val a = grid[i][n/2]
            val b = grid[m - 1 - i][n/2]

            if (a != b) {
                changeCnt++
            } else if (a == 1) oneCnt += 2
        }
    }

    ans += changeCnt
    if (oneCnt % 4 != 0 && changeCnt == 0) ans += 2


    return ans

}
}
