class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for (int i = 0 ; i < n / 2; ++i) {
            for (int j = 0 ; j < m / 2; ++j) {
                int diffs = 0;
                if (grid[i][m - 1 - j] != grid[i][j]) {
                    diffs++;
                }
                if (grid[n - 1 - i][j] != grid[i][j]) {
                    diffs++;
                }
                if (grid[n - 1 - i][m - 1 - j] != grid[i][j]) {
                    diffs++;
                }
                ans += min(diffs, 4 - diffs);
            }
        }

        if (n % 2 == 0 && m % 2 == 0) {
            return ans;
        }

        int change = 0;
        if (m % 2 != 0) {
            for (int i = 0 ; i < n / 2; ++i) {
                if (grid[n - i - 1][m / 2] != grid[i][m / 2]) {
                    change++;
                }
            }
        }
        if (n % 2 != 0) {
            for (int j = 0 ; j < m / 2; ++j) {
                if (grid[n / 2][m - j - 1] != grid[n / 2][j]) {
                    change++;
                }
            }
        }
        if (n % 2 != 0 && m % 2 != 0) {
            ans += grid[n / 2][m / 2];
        }
        // cout << change << " " << ans << " ";

        if (change > 0) {
            return ans + change;
        } else {
            int sum = 0;
            if (m % 2 != 0) {
                for (int i = 0 ; i < n; ++i) {
                    if (n % 2 == 1 && i == n / 2)
                        continue;
                    sum += grid[i][m / 2];
                }
            }

            if (n % 2 != 0) {
                for (int i = 0 ; i < m; ++i) {
                    if (m % 2 == 1 && i == m / 2)
                        continue;
                    sum += grid[n / 2][i];
                }
            }

            // cout << sum << " " << ans << " ";
            if (sum % 4 == 0) {
                return ans;
            } else {
                return ans + 2;
            }
        }
    }
};
