class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size(),res=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int cur=grid[i][j]+grid[m-1-i][j]+grid[m-1-i][n-1-j]+grid[i][n-1-j];
                // if(cur){
                //     cout<<i<<' '<<j<<' '<<cur<<endl;
                // }
                // cout<<i<<' '<<j<<' '<<cur<<endl;
                res+=(cur<=2)?cur:(4-cur);
            }
        }
        bool flag=false;
        int cur=0;
        if(m&1){
            for(int j=0;j<n/2;j++){
                if(grid[m/2][j]!=grid[m/2][n-1-j]){
                    res++;
                    flag=true;
                }
                cur+=grid[m/2][j]+grid[m/2][n-1-j];
            }
        }
        if(n&1){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-1-i][n/2]){
                    res++;
                    flag=true;
                }
                cur+=grid[i][n/2]+grid[m-1-i][n/2];
            }
        }
        if(!flag && (cur%4==2)){
            res+=2;
        }
        if(m&1 && n&1 && grid[m/2][n/2]){
            res++;
        }
        return res;
    }
};
