class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int re =0,m=grid.size(),n=grid[0].size();
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int s = grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                re+=min(s,4-s);
            }
        }
        int c01=0,c11=0;
        re+=m%2&&n%2&&grid[m/2][n/2];
        if(m%2){
            for(int i=0;i<n/2;i++){
                int s = grid[m/2][i]+grid[m/2][n-1-i];
                c01+=s==1,c11+=s==2;
            }
        }
        if(n%2){
            for(int i=0;i<m/2;i++){
                int s=grid[i][n/2]+grid[m-1-i][n/2];
                c01+=s==1,c11+=s==2;
            }
        }
        return re+c01+(c11%2 && c01 == 0 ? 2 : 0);
    }
};
