class Solution {
    public int minFlips(int[][] g) {
        // 我感觉就是如果行 / 列不一样就翻成1，然后再看看哪些是可以去掉的
        int ans = 0;
        // 如果是奇数*奇数，中间必是0，然后会有一些4 / 2的组合，4全都选cost最小，然后看看如何拼2
        int n = g.length, m = g[0].length;
        // all 4
        for (int i = 0; i < n / 2; i++) {
            // 怎么判断这个是不是4呢？
            for (int j = 0; j < m / 2; j++) {
                int one = g[i][j] + g[i][m - 1 - j] + g[n - 1 - i][j] + g[n - 1 - i][m - 1 - j];
                ans += Math.min(one, 4 - one);
            }
        }
        // 0 0 1 1 0 1
        int[] cnt = new int[3];
        int num = 0;
        // 看看是否有2的情况
        if (n % 2 == 1) {
            int i = n / 2;
            for (int j = 0; j < m / 2; j++) {
                int one = g[i][j] + g[i][m - 1 - j];
                cnt[one]++;
                if (one == 2) num += 2;
            }
        }

        if (m % 2 == 1) {
            int j = m / 2;
            for (int i = 0; i < n / 2; i++) {
                int one = g[i][j] + g[n - 1 - i][j];
                cnt[one]++;
                if (one == 2) num += 2;
            }
        }
        ans += cnt[1];
        num += cnt[1] * 2;
        // 00, 11 不用管的其实, 就是要用01把1总和变成那啥
        // 得算一下有多少01
        if (num % 4 != 0) {
            if (cnt[1] == 0) ans += 2;
        }

        if (n % 2 == 1 && m % 2 == 1 && g[(n - 1) / 2][(m - 1) / 2] == 1) ans++;
        return ans;
    }
}
