class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length, m = grid[0].length;
        int ans = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int sum = grid[i][j] + grid[n-1-i][j] + grid[i][m-1-j] + grid[n-1-i][m-1-j];
                ans += Math.min(sum, 4 - sum);
            }
        }
        int sum1 = 0, sum2 = 0;
        if (n % 2 == 1) {
            for (int i = 0; i < m / 2; i++) {
                int sum = grid[n/2][i] + grid[n/2][m-1-i];
                if (sum == 1) sum1++;
                else if (sum == 2) sum2++;
            }
        }
        if (m % 2 == 1) {
            for (int i = 0; i < n / 2; i++) {
                int sum = grid[i][m/2] + grid[n-1-i][m/2];
                if (sum == 1) sum1++;
                else if (sum == 2) sum2++;
            }
        }
        ans += sum1;
        sum2 %= 2;
        if (n % 2 == 1 && m % 2 == 1) ans += grid[n/2][m/2];
        if (sum2 != 0 && sum1 == 0) ans += 2;
        return ans;
    }
}
