class Solution {
public:
    int minFlips(vector<vector<int>>& a) {

        int m = a.size();
        int n = a[0].size();

        int ans = 0;

        for(int i = 0;i<m/2;i++)
        {
            for(int j = 0;j<n/2;j++){
                int count = a[i][j] + a[m-i-1][j] + a[i][n-j-1]+a[m-i-1][n-j-1];
                ans += min(count,4-count);
            }
        }

        int a1 = 0,a2 = 0;

        if(m%2 == 1){
            for(int j = 0;j<n/2;j++){
                if(a[m/2][j] != a[m/2][n-j-1])              a1++;
                else{
                    if(a[m/2][j] == 1)              a2 += 2;
                }
            }
        }



        if(n%2 == 1){
            for(int j = 0;j<m/2;j++){
                if(a[j][n/2] != a[m-j-1][n/2])       a1++;
                else{
                    if(a[j][n/2] == 1)              a2 += 2;
                }
            }
        }

        a2 %= 4;

        ans += a1;


        if(a2 == 2){
            if(a1 == 0)     ans += 2;
        }

        if(m%2 && n%2 && a[m/2][n/2]){
            ans++;
        }

        return ans;

    }
};
