class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int up=0;
        int down=n-1;
        int cnt=0;
        int cnt1=0;
        while(up<down){
            int left=0;
            int right=m-1;
            while(left<right){
                int num=grid[up][left]+grid[up][right]+grid[down][left]+grid[down][right];
                if(num<=2){
                    cnt+=num;
                }
                else if(num==3){
                    cnt+=1;
                }
                left++;
                right--;
            }
            up++;
            down--;
        }
        int num1=0;

        if(n%2!=0){
            int mid=n/2;
            int left=0;
            int right=m-1;
            while(left<right){
                if(grid[mid][left]!=grid[mid][right]){
                    cnt1++;
                }
                num1+=grid[mid][left]+grid[mid][right];
                left++;
                right--;
            }
        }
        if(m%2!=0){
            int mid=m/2;
            int up=0;
            int down=n-1;
            while(up<down){
                if(grid[up][mid]!=grid[down][mid]){
                    cnt1++;
                }
                num1+=grid[up][mid]+grid[down][mid];
                up++;
                down--;
            }
        }
        if((num1+cnt1)%4==0){
            cnt+=cnt1;
        }
        else if((num1+cnt1)%4==2){
            if(cnt1==0){
                cnt+=2;
            }
            else{
                cnt+=cnt1;
            }
        }
        if(m%2==1&&n%2==1){
            cnt+=grid[n/2][m/2];
        }
        return cnt;
    }
};
