class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0,one=0;
        int n=grid.size(),m=grid[0].size();
        for (int i=0;i<n/2;i++){
            for (int j=0;j<m/2;j++){
                int cur=(grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1]);
                ans+=min(cur,4-cur);
            }
        }
        int ones=0;
        bool k=0;
        if (n%2){
            for (int j=0;j<m/2;j++){
                if (grid[n/2][j]!=grid[n/2][m-j-1]) ans++,k=1;
                else if (grid[n/2][j]==1) ones++;
            }     
        }
        if (m%2){
            for (int i=0;i<n/2;i++){
                if (grid[i][m/2]!=grid[n-i-1][m/2]) ans++,k=1;
                else if (grid[i][m/2]==1) ones++;
            }     
        }
        if ((m%2) and (n%2)) ans+=grid[n/2][m/2];
        if (k==0) ans+=(ones%2)*2;
        return ans;
    }
};
