class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n,m;
        n=grid.size();
         m=grid[0].size();
         int count=0;
         for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int arr[2];
                arr[0]=0;
                arr[1]=0;
                arr[grid[i][j]]++;
                arr[grid[n-i-1][j]]++;
                arr[grid[i][m-j-1]]++;
                arr[grid[n-i-1][m-j-1]]++;
                count+=min(4-arr[0],4-arr[1]);

            }
         }
         if(n%2 and m%2){
            if(grid[n/2][m/2]==1){
                count++;
                grid[n/2][m/2]=0;
            }
         }
         int c1=0;
            int c2=0;
         if(m%2){

         for(int i=0;i<n/2;i++){

            if(grid[i][m/2]==grid[n-i-1][m/2]){
                if(grid[i][m/2]==1)
                c1++;
            }
            else{
                c2++;
            }
         }


         }
         if(n%2){
            // int c1=0;
            // int c2=0;
         for(int i=0;i<m/2;i++){

            if(grid[n/2][i]==grid[n/2][m-i-1]){
                if(grid[n/2][i]==1)
                c1++;
            }
            else{
                c2++;
            }
         }


         }
         if((c1*2)%4==0){
            count+=c2;
         }
         else{
            if(c2>0){
                count+=c2;
            }
            else{
                count+=2;
            }
         }
         return count;

    }
};
