class Solution {

    public int minFlips(int[][] grid) {
        int n = grid.length , m = grid[0].length , ans = 0;
        // check four squares
        for (int i = 0;i < n / 2;i ++) {
            for (int j = 0;j < m / 2;j ++) {
                int one = 0;
                if (grid[i][j] == 1) {
                    one ++;
                }
                if (grid[i][m - 1 - j] == 1) {
                    one ++;
                }
                if (grid[n - 1 - i][j] == 1) {
                    one ++;
                }
                if (grid[n - 1 - i][m - 1 - j] == 1) {
                    one ++;
                }
                ans += Math.min(4 - one, one);
            }
        }
        int diff = 0 , onePair = 0;
        if (n % 2 == 1) {
            int l = 0 , r = m - 1 , row = n / 2;
            while (l < r) {
                if (grid[row][l] != grid[row][r]) {
                    diff ++;
                } else {
                    if (grid[row][l] == 1) {
                        onePair ++;
                    }
                }
                l ++;
                r --;
            }
        }
        if (m % 2 == 1) {
            int l = 0 , r = n - 1 , col = m / 2;
            while (l < r) {
                if (grid[l][col] != grid[r][col]) {
                    diff ++;
                } else {
                    if (grid[l][col] == 1) {
                        onePair ++;
                    }
                }
                l ++;
                r --;
            }
        }
        // handle one pair and diff
        if (onePair % 2 == 1) {
            if (diff > 0) {
                ans += diff;
            } else {
                ans += 2;
            }
        } else {
            ans += diff;
        }
        // center
        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans ++;
            }
        }
        return ans;
    }

}
