class Solution {
    public int minFlips(int[][] grid) {
        int n=grid.length,m=grid[0].length,op=0;
            for(int j=0;j<n/2;j++){
                for(int k=0;k<m/2;k++){
                    int cnt1=0;
                    if(grid[j][k]==1){
                        cnt1++;
                    }
                    if(grid[n-j-1][k]==1){
                        cnt1++;
                    }
                    if(grid[j][m-k-1]==1){
                        cnt1++;
                    }
                    if(grid[n-j-1][m-k-1]==1){
                        cnt1++;
                    }
                    op+=Math.min(cnt1,4-cnt1);
                }
        }
        int matched=0,diff=0;
        if(n%2!=0){
            for(int g=0;g<m/2;g++){
                if(grid[n/2][g]!=grid[n/2][m-g-1]){
                    diff++;
                }else if(grid[n/2][g]==1)
                matched++;
            }
        }
        if(m%2!=0){
            for(int g=0;g<n/2;g++){
                if(grid[g][m/2]!=grid[n-g-1][m/2]){
                    diff++;
                }else if(grid[g][m/2]==1){
                    matched++;
                }

            }
        }
        if(matched%2!=0 && diff==0){
           op+=2;
        }
        op+=diff;
        if(n%2!=0 && m%2!=0 && grid[n/2][m/2]==1){
            op++;
        }
        return op;
    }
}
