class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        hm, hn = m // 2, n // 2
        ans = 0
        # 将矩阵对折两次，为使重叠的四个部分（不包括折痕）相等，最少需要翻转几次
        for i in range(hm):
            for j in range(hn):
                temp = grid[i][j] + grid[i][-1 - j] + grid[-1 - i][j] + grid[-1 - i][-1 - j]
                # 需要temp为0或4
                ans += temp if temp < 2 else 4 - temp

        # 为使两条折痕（不包括中心）对称，最少需要翻转几次
        temp = 0  # 两条折痕上有多少对1
        flag = False  # 两条折痕上有没有不对称的点
        if m & 1:
            for j in range(hn):
                if grid[hm][j] != grid[hm][-1 - j]:
                    ans += 1  # 翻转一次，不妨设都翻转为0
                    flag = True
                else:
                    temp += grid[hm][j]
        if n & 1:
            for i in range(hm):
                if grid[i][hn] != grid[-1 - i][hn]:
                    ans += 1  # 翻转一次，不妨设都翻转为0
                    flag = True
                else:
                    temp += grid[i][hn]
        # 两条折痕应该有偶数对1
        # 否则，如果flag为True，可以把某一次翻转为0改成翻转为1
        # 否则，需要翻转已有的某一对
        if temp & 1 and not flag:
            ans += 2

        # 为满足题意，中心必须为0
        ans += grid[hm][hn] if m & 1 and n & 1 else 0
        return ans
