class Solution {
public:
    int func(map<pair<int,int>,int> &mp,vector<vector<int>>&grid){
        int cnt0=0,cnt1=0;
        for(auto it:mp){
            int x=it.first.first;
            int y=it.first.second;
            if(grid[x][y]==0){
                cnt0++;
            }
            else{
                cnt1++;
            }
        }
        return min(cnt0,cnt1);
    }
    int func1(map<pair<int,int>,int> &mp,vector<vector<int>>&grid){
        int cnt0=0,cnt1=0;
        for(auto it:mp){
            int x=it.first.first;
            int y=it.first.second;
            if(grid[x][y]==0){
                cnt0++;
            }
            else{
                cnt1++;
            }
        }
        if(cnt1==2){
            return 11;
        }
        if(cnt0==2){
            return 0;
        }
        return 1;
    }
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int cnt11=0,cnt10=0,cnt00=0;
        int n=grid.size();
        int m=grid[0].size();
        vector<vector<int>> vis(n,vector<int>(m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]){
                    continue;
                }
                map<pair<int,int>,int> mp;
                vis[i][j]=1;
                vis[n-i-1][j]=1;
                vis[i][m-j-1]=1;
                vis[n-i-1][m-j-1]=1;
                mp[{i,j}]++;
                mp[{n-i-1,j}]++;
                mp[{i,m-j-1}]++;
                mp[{n-i-1,m-j-1}]++;
                if(mp.size()==4){
                    ans+=func(mp,grid);
                }
                else if(mp.size()==2){
                    if(func1(mp,grid)==11){
                        cnt11++;
                    }
                    else if(func1(mp,grid)==1){
                        cnt10++;
                    }
                    else{
                        cnt00++;
                    }
                }
                else{
                    if(grid[i][j]==1){
                        ans+=1;
                    }
                }
            }
        }
        // cout<<cnt11<<" "<<cnt10<<" "<<cnt00<<endl;
        if(cnt11%2 && cnt10==0){
            ans+=2;
        }
        ans+=cnt10;
        return ans;
    }
};
