class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int m = g.size(), n = g.front().size();
        int ans{};
        for(int i = 0; i < m/2; i ++){
            for(int j = 0; j < n/2; j ++){
                if(g[i][j] != g[i][n-1-j] || g[i][j] != g[m-1-i][j] || g[i][j] != g[m-1-i][n-1-j]){
                    int x = g[i][j] + g[i][n-1-j] + g[m-1-i][j] + g[m-1-i][n-1-j];
                    if(x == 2) ans+=2;
                    else ans += 1;
                }
            }
        }

        if((m&1) && (n&1) && g[m/2][n/2]){
            ans++;
            g[m/2][n/2] = 0;
        }

        int cnt1{}, cnt2{}, cnt3{};
        if(m & 1){
            for(int i = 0; i < n/2; i ++){
                if(g[m/2][i] == g[m/2][n-1-i]){
                    cnt1++;
                    cnt3 += g[m/2][i] * 2;
                }else{
                    cnt2++;
                }
            }
        }

        if(n & 1){
            for(int i = 0; i < m/2; i ++){
                if(g[i][n/2] == g[m-1-i][n/2]){
                    cnt3 += g[i][n/2] * 2;
                    cnt1++;
                }else{
                    cnt2++;
                }
            }
        }
        // cout << cnt1 << " " << cnt2 << " " << cnt3 << endl;

        if(cnt3 % 4 == 0 || cnt2 > 0){
            return ans + cnt2;
        }

        return ans + cnt2 + 2;
    }
};
