class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
         int rc = 0 ;
        int co = 0;
        int n  = grid.size();
        int m = grid[0].size();
        int oops = 0,zops = 0;

        for(int x=0;x<n/2;x++)
        {
            for(int y=0;y<m/2;y++)
            {
                int co = 0;
                co +=  grid[x][y];
                co += grid[x][m-y-1];
                co += grid[n-x-1][y];
                co += grid[n-x-1][m-y-1];

                if(co!=0 && co!=4)
                {
                    if(co>=2)
                    {
                        grid[x][y] = 1;
                        grid[x][m-y-1] = 1;
                        grid[n-x-1][y] = 1;
                        grid[n-x-1][m-y-1] = 1;

                        oops += 4 -co;
                    }
                    else
                    {
                          grid[x][y] = 0;
                        grid[x][m-y-1] = 0;
                        grid[n-x-1][y] = 0;
                        grid[n-x-1][m-y-1] = 0;

                        zops += co;
                    }
                }
            }
        }
        int onc = 0;
        int ans = 0;
        if(n%2!=0)
        {
            int ro = n/2;
            for(int x=0;x<m/2;x++)
            {
                if(grid[ro][x]!=grid[ro][m-x-1])
                {
                    ans++;
                }
                else if(grid[ro][x]==1)
                    onc += 2;
            }
        }

        if(m%2!=0)
        {   
             int col = m/2;
            for(int x=0;x<n/2;x++)
            {
                if(grid[x][col]!=grid[n-x-1][col])
                {
                    ans++;
                }
                else if(grid[x][col]==1)
                    onc += 2;
            }

        }
        int fans  =0 ;
        fans += oops+zops;

        if(n%2!=0 && m%2!=0)
        {
            if(grid[n/2][m/2]==1)
                fans++;
        }

        if(onc%4!=0)
        {
            if(ans==0)
                fans += 2;
        }



        fans += ans;
        return fans;
    }
};
