class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        int res = 0;
        int orig_1 = 0;
        bool fixable = false;
        if (m % 2 == 1) {
            auto &mid = grid[m / 2];
            if (n % 2 == 1) {
                if (mid[n / 2] == 1) {
                    res++;
                    mid[n / 2] = 0;
                }
            }

            for (int i = 0; i < n / 2; i++) {
                if (mid[i] != mid[n - 1 - i]) {
                    res++;
                    fixable = true;
                } else if (mid[i] == 1) {
                    orig_1++;
                }
            }
        }

        if (n % 2 == 1) {
            int mid = n / 2;
            for (int i = 0; i < m / 2; i++) {
                int a = grid[i][mid];
                int b = grid[m - 1 - i][mid];
                if (a != b) {
                    res++;
                    fixable = true;
                } else if (a == 1) {
                    orig_1++;
                }
            }
        }

        if (orig_1 % 2 == 1 && !fixable) {
            res += 2;
        }

        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                int a = grid[i][j];
                int b = grid[i][n - 1 - j];
                int c = grid[m - 1 - i][j];
                int d = grid[m - 1 - i][n - 1 - j];
                int sum = a + b + c + d;
                if (sum == 1 || sum == 3) {
                    res += 1;
                } else if (sum == 2) {
                    res += 2;
                }
            }
        }

        return res;
    }
};
