class Solution {
public:
    int minFlips(vector<vector<int>>& v1) {
        int n=v1.size(); int m=v1[0].size();
        int count=0; int one=0, two=0, zero=0;
        for(int i=0; i<(n+1)/2; i++){
            for(int j=0; j<(m+1)/2; j++){
                int a=0; int val;
                if( i== n-1-i && j != m-j-1){
                    a=v1[i][j]+v1[i][m-j-1]; 
                    if( a==1) two++;
                    else if(a==2) one++;
                    else          zero++;

                }else if( j== m-j-1 && i != n-1-i){
                    a=v1[i][j]+v1[n-1-i][j]; 
                    if( a==1) two++;
                    else if(a==2) one++;
                    else          zero++;

                }else if( i==n-1-i && j== m-1-j){
                    if( v1[i][j]==1) count++;
                }else{
                    val=4; a= v1[i][j]+v1[n-i-1][j]+v1[i][m-j-1]+v1[n-1-i][m-1-j];
                    count= count+ min( val-a, val-(val-a));
                }

            }
        }

        if( two != 0){
            count = count+two;
        }else if( two ==0 && one%2 ==1 ){
            count=count+2;
        }

        return count;
    }
};
