class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();
        int res = 0;
        for(int i = 0; i < n / 2; ++i) {
            for(int j = 0; j < m / 2; ++j) {
                int cnt = 0;
                cnt = (a[i][j] + a[n-1-i][j] + a[i][m-1-j] + a[n-1-i][m-1-j]);
                if(cnt == 1 || cnt == 3) res++;
                if(cnt == 2) res += 2;
            }
        }
        bool has_change = 0;
        int cnt_1 = 0;
        if(n % 2 == 1) {
            for(int i = 0; i < m / 2; ++i) {
                if(a[n/2][i] != a[n/2][m-1-i]) {
                    has_change = 1;
                    res++;
                }
                else {
                    cnt_1 += a[n/2][i] + a[n/2][m-1-i];
                }
            }
        }
        if(m % 2 == 1) {
            for(int i = 0; i < n / 2; ++i) {
                if(a[i][m/2] != a[n-i-1][m/2]) {
                    has_change = 1;
                    res++;
                }
                else {
                    cnt_1 += a[i][m/2] + a[n-i-1][m/2];
                }
            }
        }
        if(cnt_1 % 4 == 2 && has_change == false)
            res += 2;
        if((n % 2 == 1) && (m % 2 == 1)) {
            res += a[n/2][m/2];
        }
        return res;
    }
};
