class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int an1=0,n=g.size(),m=g[0].size();
        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                vector<int> v(2,0);
                v[g[i][j]]++;
                v[g[i][m-j-1]]++;
                v[g[n-i-1][j]]++;
                v[g[n-i-1][m-j-1]]++;
                an1+=min(v[0],v[1]);
            }
        }
        int c=0,to=0;
        if(n%2)
            for(int i=0;i<m/2;i++) {
                if(g[n/2][i]!=g[n/2][m-i-1]) { an1++; to++;}
                else if(g[n/2][i]) c+=2;
            }


        if(m%2)
            for(int i=0;i<n/2;i++) {
                if(g[i][m/2]!=g[n-i-1][m/2]) { an1++; to++;}
                else if(g[i][m/2]) c+=2;
            }

        if(m%2 && n%2) {
            if(g[n/2][m/2]) c++;
        }
        // cout<<c<<" "<<to<<endl;
        if(c%4>1 && c%4<4 && to) c+=2;

        return an1+c%4;

    }
};
