class Solution {
public:

    int finddist(vector<int>& v1,vector<int>& v2,vector<int>& v3,vector<int>& v4){
        set<vector<int>> s;
        s.insert(v1);
        s.insert(v2);
        s.insert(v3);
        s.insert(v4);
        return s.size();
    }

    void markall(vector<vector<int>>& grid,vector<int>&v){
        grid[v[0]][v[1]]=-1;    
    }


    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n = grid.size();
        int m = grid[0].size();
        int casesoftwos=0;
        int casesofones=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==-1) continue;
                else{
                    vector<int> curr = {i,j};
                    vector<int> right = {n-i-1,j};
                    vector<int> down = {i,m-j-1};
                    vector<int> downright = {n-i-1,m-j-1};
                    int ones=0;
                    if(finddist(curr,right,down,downright)==4){
                        if(grid[curr[0]][curr[1]]==1) ones++;
                        if(grid[right[0]][right[1]]==1) ones++;
                        if(grid[down[0]][down[1]]==1) ones++;
                        if(grid[downright[0]][downright[1]]==1) ones++;
                        if(ones==2) ans+=2;
                        else if(ones==1 || ones==3) ans+=1;
                        else ans+=0;
                    }
                    else if(finddist(curr,right,down,downright)==2){
                        if(curr==right){
                            if(grid[curr[0]][curr[1]]==1) ones++;
                            if(grid[down[0]][down[1]]==1) ones++;
                        }
                        else{
                            if(grid[curr[0]][curr[1]]==1) ones++;
                            if(grid[downright[0]][downright[1]]==1) ones++;
                        }
                        if (ones==1){
                            casesofones++;
                            ans++;
                        }
                        else if(ones==2){
                            casesoftwos++;
                        }
                    }
                    else{
                        if (grid[i][j]!=0){
                            ans++;
                        }
                    }
                    markall(grid,curr);
                    markall(grid,right);
                    markall(grid,down);
                    markall(grid,downright);
                }
            }
        }
        if(casesoftwos%2!=0 && casesofones==0){
            ans+=2;
        }
        return ans;
    }
};
