class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),row=0,col=0,fans=0;
        for(int i=0;i<(n/2);i++){
            for(int j=0;j<(m/2);j++){
                map<int,int> mp;
                mp[grid[i][j]]++;
                mp[grid[i][m-1-j]]++;
                mp[grid[n-1-i][j]]++;
                mp[grid[n-1-i][m-1-j]]++;

                fans+=min(4-mp[0],4-mp[1]);
            }
        }
        int c1=0,buffer=0;
        if(n%2==1){
            for(int j=0;j<(m/2);j++){
                if(grid[(n-1)/2][j]!=grid[(n-1)/2][m-1-j]){
                    fans++;
                    buffer++;
                    continue;
                }
                else{
                    if(grid[(n-1)/2][j]==1){c1+=2;}
                }
            }
        }
        if(m%2==1){
            for(int i=0;i<(n/2);i++){
                if(grid[i][(m-1)/2]!=grid[n-1-i][(m-1)/2]){
                    fans++;
                    buffer++;
                    continue;
                }
                else{
                    if(grid[i][(m-1)/2]==1){c1+=2;}
                }
            }
        }
        if((n%2==1)&&(m%2==1)){
            if(grid[n/2][m/2]==1){fans++;}
        }
        cout<<c1<<" "<<buffer<<"\n";
        if(c1%4==2){
            if(buffer>0){
                return fans;
            }
            else{
                fans+=2;
            }
        }

        return fans;
    }
};
