class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        if(grid.size() == 0){
            return 0;
        }
        int res = 0;
        int m = grid.size(), n = grid[0].size();
        for(int i = 0; i < m / 2; i++){
            for(int j = 0; j < n / 2; j++){
                int num1 = 0;
                // 统计四个
                if(grid[i][j] == 1){
                    num1++;
                }
                if(grid[i][n - 1 - j] == 1){
                    num1++;
                }
                if(grid[m - 1 - i][j] == 1){
                    num1++;
                }
                if(grid[m - 1 - i][n - 1 - j] == 1){
                    num1++;
                }
                res += min(num1 , 4 - num1);
            }
        }
        // 处理中间情况 1的数量以及不同的对数
        int diff = 0, num1 = 0;
        if(m % 2 != 0){
            int row = m / 2;
            for(int j = 0; j < n / 2; j++){
                if(grid[row][j] != grid[row][n - 1 - j]){
                    diff++;
                }else if(grid[row][j] == 1){
                    num1 += 2;
                }
            }
        }
        if(n % 2 != 0){
            int col = n / 2;
            for(int j = 0; j < m / 2; j++){
                if(grid[j][col] != grid[m - 1 - j][col]){
                    diff++;
                }else if(grid[j][col] == 1){
                    num1 += 2;
                }
            }
        }
        res += diff;
        if(num1 % 4 != 0 && diff == 0){
            res += 2;
        }
        if(m % 2 != 0 && n % 2 != 0 && grid[m / 2][n / 2] == 1){
            res++;
        }

        return res;

    }
};
