class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int res = 0;
        int one;
        for (int i = 0; i < m/2; i++) {
            for (int j = 0; j < n/2; j++) {
                one = 0;
                one += grid[i][j];
                one += grid[m - i - 1][j];
                one += grid[i][n - 1 - j];
                one += grid[m - 1 - i][n - 1 - j];
                if (one < 2) res += one;
                else res += 4 - one;
            }
        }
        int mRes = 0;
        int nRes = 0;
        if (m % 2 == 1) {
            one = 0;
            for (int i = 0; i < n/2; i++) {
                if (grid[m/2][i] != grid[m/2][n - i - 1]) mRes++;
                else if (grid[m/2][i] == 1) one += 2;
            }
            if (n % 2 == 1) {
                for (int i = 0; i < m/2; i++) {
                    if (grid[i][n/2] != grid[m - i - 1][n/2]) nRes++;
                    else if (grid[i][n/2] == 1) one += 2;
                }
                res += grid[m/2][n/2];
                if (one % 4 == 2 && mRes == 0 && nRes == 0) res += 2;
            } else {
                if (one % 4 == 2 && mRes == 0) res += 2;
            }
        } else if (n % 2 == 1) {
            one = 0;
            for (int i = 0; i < m/2; i++) {
                if (grid[i][n/2] != grid[m - i - 1][n/2]) nRes++;
                else if (grid[i][n/2] == 1) one += 2;
            }
            if (one % 4 == 2 && nRes == 0) res += 2;
        }
        res += mRes;
        res += nRes;
        return res;
    }
};
