class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n = len(grid) ,len(grid[0])
        out = 0
        rl , rr = 0 , m-1
        flg = False
        sm = 0
        while rl <= rr:
            cl , cr = 0 , n-1
            while cl <= cr:
                if cl == cr and rl == rr:
                    if grid[rl][cl]: out += 1
                elif cl == cr or rl == rr:
                    k = (grid[rl][cl] + grid[rr][cl] + grid[rl][cr] + grid[rr][cr])//2
                    if k == 1:
                        flg = True
                        out += 1
                        sm += 2
                    elif k == 2: sm += 2
                else:
                    k = grid[rl][cl] + grid[rr][cl] + grid[rl][cr] + grid[rr][cr]
                    if k == 1 or k == 3:
                        out += 1
                    elif k == 2:
                        out += 2
                cl += 1
                cr -= 1
            rl += 1
            rr -= 1
        if sm%4 and (m&1 or n&1) and not flg:
            out += 2
        return out
