class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int ans = 0;
        for(int i = 0; i < m / 2; ++i) {
            for(int j = 0; j < n / 2; ++j) {
                int cnt = 0;
                if(grid[i][j]) ++cnt;
                if(grid[i][n - 1 - j]) ++cnt;
                if(grid[m - 1 - i][j]) ++cnt;
                if(grid[m - 1 - i][n - 1 - j]) ++cnt;
                ans += min(cnt, 4 - cnt);
            }
        }
        if((m & 1) || (n & 1)) {
            int cnt = 0, c = 0;
            if(m & 1) {
                int tmd = m / 2;
                for(int i = 0; i < n; ++i) {
                    if(i < n / 2 && grid[tmd][i] != grid[tmd][n - 1 - i]) ++c;
                    if(grid[tmd][i]) ++cnt; 
                }
            }
            if(n & 1) {
                int tmd = n / 2;
                for(int i = 0; i < m; ++i) {
                    if(i < m / 2 && grid[i][tmd] != grid[m - 1 - i][tmd]) ++c;
                    if(grid[i][tmd]) ++cnt;
                }
            }
            if((m & 1) && (n & 1) && grid[m / 2][n / 2]) {
                cnt -= 2;
                ++ans;
            }
            if(cnt > c && ((cnt - c) % 4 == 2) && c == 0) ans += 2;
            ans += c;
        }
        return ans;
    }
};
