class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(),i,j,k,l,ans=0;
        for(i=0;i<n/2;i++){
            for(j=0;j<m/2;j++){

                k=0;
                if(grid[i][j]) k++;
                if(grid[n-1-i][j]) k++;
                if(grid[n-1-i][m-1-j]) k++;
                if(grid[i][m-1-j]) k++;
                ans+=min(k,4-k);
            }

        }
        if(n%2 && m%2==0){
            int a =0, b = 0, c =0;
            for(i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-1-i]) b++;
                else{
                    if(grid[n/2][i]) a++;
                    else c++;
                }
            }
            if(a%2==0) ans+=b;
            else {
                if(!b){
                    if(a) ans+=2;
                }
                else ans+=b;
            }
        }
        if(m%2 && n%2 ==0){
            int a =0, b = 0, c =0;
            for(i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]) b++;
                else{
                    if(grid[n-1-i][m/2]) a++;
                    else c++;
                }
            }
            if(a%2==0) ans+=b;
            else {
                if(!b){
                    if(a) ans+=2;
                }
                else ans+=b;
            }
        }
        if(n%2 && m%2){
            int a =0, b = 0, c =0;
            for(i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]) b++;
                else{
                    if(grid[n-1-i][m/2]) a++;
                    else c++;
                }
            }
            for(i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-1-i]) b++;
                else{
                    if(grid[n/2][i]) a++;
                    else c++;
                }
            }
            if(a%2==0) ans+=b;
            else {
                if(!b){
                    if(a) ans+=2;
                }
                else ans+=b;
            }
        }
        if(n%2 && m%2 && grid[n/2][m/2]) ans++;
        return ans;
    }

};
