class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt1=0;
                int cnt2=0;
                if(grid[i][j]==1)cnt1++;
                else cnt2++;
                if(grid[i][m-1-j]==1)cnt1++;
                else cnt2++;
                if(grid[n-1-i][j]==1)cnt1++;
                else cnt2++;
                if(grid[n-1-i][m-1-j]==1)cnt1++;
                else cnt2++;
                ans+=min(cnt1,cnt2);
            }
        }
        int value1=0;
        int cnter1=0;
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2])value1+=2;
                else if(grid[i][m/2])cnter1+=2;
            }
        }
        int value2=0;
        int cnter2=0;
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-1-i])value2+=2;
                else if(grid[n/2][i])cnter2+=2;
            }
        }
        if(value1%2==0 && value2%2==0){
            if((cnter1+cnter2)%4!=0 && value1==0 && value2==0)ans+=2;
        }
        else ans+=(cnter1%2 + cnter2%2);
        ans+=(value1/2 + value2/2);
        if(n%2 && m%2 && grid[n/2][m/2])ans++;
        return ans;
    }
};
