class Solution(object):
    def minFlips(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        m=len(grid)
        n=len(grid[0])
        re=0
        for i in range(m//2):
            for j in range(n//2):
                if grid[i][j]!=grid[i][n-1-j] or grid[i][j]!=grid[m-1-i][j] or grid[i][j]!=grid[m-1-i][n-1-j]:
                    t=sum([grid[i][j],grid[i][n-1-j],grid[m-1-i][j],grid[m-1-i][n-1-j]])
                    re+=min(t,4-t)
        temp1=temp2=0
        s1=s2=0
        if m%2:
            for j in range(n//2):
                if grid[m//2][j]!=grid[m//2][n-1-j]:
                    temp1+=1
                s1+=grid[m//2][j]
                s1+=grid[m//2][n-1-j]
        if n%2:
            for i in range(m//2):
                if grid[i][n//2]!=grid[m-1-i][n//2]:
                    temp2+=1
                s2+=grid[i][n//2]
                s2+=grid[m-1-i][n//2]
        if temp1>0 or temp2>0:
            re+=temp1+temp2
        else:
            if (s1+s2)%4!=0:
                re+=2
        if m%2 and n%2:
            re+=grid[m//2][n//2]
        return re
