class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        //n*m
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cur=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                ans+=min(cur,4-cur);
            }
        }
        int an=0,am=0,an1=0,am1=0;
        if(n%2==1){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-1-j]){
                    an++;
                    an1++;
                }
                else{
                    an1+=(grid[n/2][j]+grid[n/2][m-1-j]);
                }
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]){
                    am++;
                    am1++;
                }
                else{
                    am1+=(grid[i][m/2]+grid[n-1-i][m/2]);
                }
            }
        }
        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1){
                ans++;
            }
        }
        ans+=(am+an);
        if(am==0 && an==0){
            ans+=(am1+an1)%4;
        }
        return ans;
    }
};
