class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n,m = len(grid),len(grid[0])

        ans = 0
        for i in range(0,n//2):
            for j in range(0,m//2):
                x = grid[i][j] + grid[i][m-1-j] + grid[n-1-i][j] + grid[n-1-i][m-1-j]
                ans += min(x,4-x)

        cnt = 0
        one = 0
        if n % 2 == 1:
            i,j = 0,m-1
            while i<j:
                if grid[n//2][i] != grid[n//2][j]:
                    cnt+=1
                else:
                    one += grid[n//2][i]+grid[n//2][j]
                i+=1
                j-=1
        if m % 2 == 1:
            i,j = 0,n-1
            while i<j:
                if grid[i][m//2] != grid[j][m//2]:
                    cnt+=1
                else:
                    one += grid[i][m//2] + grid[j][m//2]
                i+=1
                j-=1

        if n%2==1 and m%2==1:
            ans += grid[n//2][m//2]
        if cnt == 0:
            if one % 4 != 0:
                ans += 2
        else:
            ans += cnt

        return ans
