class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;
        for(int i = 0; i < m / 2; ++i) {
            for(int j = 0; j < n / 2; ++j) {
                int num0 = 0, num1 = 1;
                if(grid[i][j] == grid[i][n - j - 1]) num1++;
                else num0++;
                if(grid[i][j] == grid[m - i - 1][j]) num1++;
                else num0++;
                if(grid[i][j] == grid[m - i - 1][n - j - 1]) num1++;
                else num0++;
                if(num0 == 0 || num0 == 4) continue;
                else if(num0 == 2) ans += 2;
                else ans += 1;
            }
        }
        if(m & 1 && n & 1) {
            if(grid[m / 2][n / 2] == 1) ++ans;
            int num = 0;
            int s1 = 0;
            for(int i = 0; i < m / 2; ++i)
                if(grid[i][n / 2] != grid[m - i - 1][n / 2]) num++;
                else if(grid[i][n / 2] == 1) ++s1;
            for(int i = 0; i < n / 2; ++i)
                if(grid[m / 2][i] != grid[m / 2][n - i - 1]) num++;
                else if(grid[m / 2][i] == 1) ++s1;
            if(s1 & 1) {
                if(num > 0) ans += num;
                else ans += 2;
            } else ans += num;
        } else if(m & 1) {
            int num = 0;
            int s1 = 0;
            for(int i = 0; i < n / 2; ++i)
                if(grid[m / 2][i] != grid[m / 2][n - i - 1]) ++num;
                else if(grid[m / 2][i] == 1) ++s1;
            if(s1 & 1) {
                if(num > 0) ans += num;
                else ans += 2;
            } else ans += num;
        } else if(n & 1) {
            int num = 0;
            int s1 = 0;
            for(int i = 0; i < m / 2; ++i)
                if(grid[i][n / 2] != grid[m - i - 1][n / 2]) ++num;
                else if(grid[i][n / 2] == 1) ++s1;
            if(s1 & 1) {
                if(num > 0) ans += num;
                else ans += 2;
            } else ans += num;
        }
        return ans;
    }
};
