class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans1=0;
        for(int i=0; i<n/2; i++) {
            for(int j=0; j<m/2; j++) {
                int val=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                ans1+=min(val, 4-val);
            }
        }
        int change=0;
        int one=0;
        if(n%2) {
            int idx=n/2;
            for(int i=0; i<m/2; i++) {
                if(grid[idx][i]!=grid[idx][m-1-i]) {
                    change++;
                } else if(grid[idx][i]==1) {
                    one+=2;
                }
            }
        }
        if(m%2) {
            int idx=m/2;
            for(int i=0; i<n/2; i++) {
                if(grid[i][idx]!=grid[n-1-i][idx]) {
                    change++;
                }
                else if(grid[i][idx]==1) {
                    one+=2;
                }
            }
        }
        if(one%4!=0) {
            if(change==0) {
                ans1+=2;
            }
        }
        if(n%2 && m%2) {
            ans1+=(grid[n/2][m/2]);
        }

        return ans1+change;
    }
};
