class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int m=grid.size(),n=grid[0].size();
        for (int i=0;i<m/2;i++)
            for (int j=0;j<n/2;j++){
                int tmp=grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-i-1][n-j-1];
                ans+=min(tmp,4-tmp);
            }
        if (m%2==1 && n%2==1){
            int x=0,y=0;
            for (int i=0;i<n/2;i++){
                int u=grid[m/2][i],v=grid[m/2][n-i-1];
                if (u==1 & v==1)
                    x++;
                if (u+v==1)
                    y++;
            }
            for (int i=0;i<m/2;i++){
                int u=grid[i][n/2],v=grid[m-i-1][n/2];
                if (u==1 & v==1)
                    x++;
                if (u+v==1)
                    y++;
            }
            if ((x*2)%4==0)
                ans+=y;
            else{
                if (y==0)
                    ans+=2;
                else
                    ans+=y;
            }
            if (grid[m/2][n/2])
                ans++;
        }
        else if (m%2==1){
            int x=0,y=0;
            for (int i=0;i<n/2;i++){
                int u=grid[m/2][i],v=grid[m/2][n-i-1];
                if (u==1 & v==1)
                    x++;
                if (u+v==1)
                    y++;
            }
            if ((x*2)%4==0)
                ans+=y;
            else{
                if (y==0)
                    ans+=2;
                else
                    ans+=y;
            }
        }
        else if (n%2==1){
            int x=0,y=0;
            for (int i=0;i<m/2;i++){
                int u=grid[i][n/2],v=grid[m-i-1][n/2];
                if (u==1 & v==1)
                    x++;
                if (u+v==1)
                    y++;
            }
            if ((x*2)%4==0)
                ans+=y;
            else{
                if (y==0)
                    ans+=2;
                else
                    ans+=y;
            }
        }
        return ans;
    }
};
