class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt=0;
                if(grid[i][j]==1) cnt++;
                if(grid[n-1-i][j]==1) cnt++;
                if(grid[i][m-j-1]==1) cnt++;
                if(grid[n-1-i][m-1-j]==1) cnt++;
                ans+=min(cnt,4-cnt);
            }
        }
        int count1=0;
        int temp=0;
        if(n%2!=0){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-1-j]) temp++;
                else if(grid[n/2][j]==1) count1+=2;
            }
        }
        int count2=0;
        if(m%2!=0){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]) temp++;
                else if(grid[i][m/2]==1) count2+=2;
            }
        }
        if(temp==0&&(count1+count2)%4!=0) temp=2;
        ans+=temp;
        if(n%2!=0&&m%2!=0&&grid[n/2][m/2]==1) ans++;
        return ans;
    }
};
