class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans=inf;
//        int O=0;
//        REP(i,n) REP(j,m) O+=grid[i][j];
        vector<int> dp(4, inf9);
        dp[0] = 0;
        for(int i=0;i<=(n-1)/2;i++) {
            for(int j=0;j<=(m-1)/2;j++) {
                set<pii> sp;
                sp.insert({i,j});
                sp.insert({i,m-1-j});
                sp.insert({n-1-i,j});
                sp.insert({n-1-i,m-1-j});
                int A = sp.size();
                int one = 0;
                for(auto &p: sp) one+=grid[p.first][p.second];

                //all - 1
                int nd = A - one;
                vector<int> ndp(4,inf9);
                REP(x,4) mintt(ndp[(x+A)%4], dp[x] + nd);
                //all - 0
                nd = one;
                REP(x,4) mintt(ndp[x], dp[x] + nd);
                swap(dp,ndp);
            }
        }
        return dp[0];
    }
};
