class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();


        int ans = 0;
        for(int i = 0;i<n/2;i++)
        {
            for(int j = 0;j<m/2;j++)
            {
                int cnt = grid[i][j] + grid[i][m-1-j] + grid[n-1-i][j] + grid[n-1-i][m-1-j];
                cnt = min(cnt,4 - cnt);
                ans +=cnt;
            }
        }
        cout<<ans<<endl;

        if(n%2 == 0 and m%2 == 0)
           return ans;
        if(n%2 and m%2 and grid[n/2][m/2]) ans++;

        int ek = 0,doo = 0;

        if(n%2)
        {
          for(int j = 0;j<m/2;j++)
           {
            int cnt = grid[n/2][j] + grid[n/2][m-1-j];
            if(cnt == 1) ek++;
            if(cnt == 2) doo++;
           }  
        }

        if(m%2)
        {
          for(int i = 0;i<n/2;i++)
           {
            int cnt = grid[i][m/2] + grid[n-1-i][m/2];
            if(cnt == 1) ek++;
            if(cnt == 2) doo++;
           }  
        }

        ans += ek;
        if(ek > 0) return ans;

        if(doo%2) ans +=2;
        return ans;




    }
};
