class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int n=arr.size(), m=arr[0].size();
        int ans=0;
        for(int i=0;i<(n/2);i++){
            for(int j=0;j<(m/2);j++){
                int c=0;
                if(arr[i][j]) c++;
                if(arr[n-i-1][j]) c++;
                if(arr[n-i-1][m-j-1]) c++;
                if(arr[i][m-j-1]) c++;
                ans+=min(c, 4-c);
            }
        }
        if(n&1 && m&1){
            int pos=0, c=0;
            for(int j=0;j<(m/2);j++){
                if(arr[n/2][j]!=arr[n/2][m-j-1]) pos++;
                else if(arr[n/2][j]) c+=2;
            }
            for(int i=0;i<(n/2);i++){
                if(arr[i][m/2]!=arr[n-i-1][m/2]) pos++;
                else if(arr[i][m/2]) c+=2;
            }
            if(arr[n/2][m/2]) ans++;
            if(pos==0){
                if(c%4==2){
                    ans+=2;
                }
            }
            else ans+=pos;
        }
        else if(n&1){
            int pos=0, c=0;
            for(int j=0;j<(m/2);j++){
                if(arr[n/2][j]!=arr[n/2][m-j-1]) pos++;
                else if(arr[n/2][j]) c+=2;
            }
            if(pos==0){
                if(c%4==2){
                    ans+=2;
                }
            }
            else ans+=pos;
        }
        else if(m&1){
            int pos=0, c=0;
            for(int i=0;i<(n/2);i++){
                if(arr[i][m/2]!=arr[n-i-1][m/2]) pos++;
                else if(arr[i][m/2]) c+=2;
            }
            if(pos==0){
                if(c%4==2){
                    ans+=2;
                }
            }
            else ans+=pos;
        }
        return ans;
    }
};
