class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;

        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int curr=0;
                curr+=(grid[n-1-i][j]);
                curr+=(grid[i][m-1-j]);
                curr+=(grid[i][j]);
                curr+=(grid[n-1-i][m-1-j]);
                if (curr>2){
                    ans+=curr%2;
                }
                else ans+=curr;

            }
        }
        int m_cnt=0;
        int f_cnt=0;
        if(n%2!=0){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-1-i]){
                    f_cnt++;
                     ans++;
                }
                else m_cnt+=2*grid[n/2][i];
            }
        }

        if(m%2!=0){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]){
                    f_cnt++;
                    ans++;
                }
                else m_cnt+=2*grid[i][m/2];
            }
        }
        if(n%2!=0&&m%2!=0){
            if(grid[n/2][m/2]==1) ans++;
        }
        if(m_cnt%4!=0){
            if(f_cnt==0) ans+=2;
        }
        return ans;
    }
};
