class Solution {
public:
    int minFlips(vector<vector<int>>& ar) {
        int n = ar.size(), m = ar[0].size();
        vector<vector<int>>tr(n + 1, vector<int>(m + 1));
        for(int i = 1; i <= n; ++i){
            for(int j = 1; j <= m; ++j){
                tr[i][j] = ar[i - 1][j - 1];
            }
        }
        int ans = 0;
        for(int i = 1; i <= n / 2; ++i){
            for(int j = 1; j <= m / 2; ++j){
                int one = 0, zeor = 0;
                if(tr[i][j] == 0)++zeor;
                else ++one;
                if(tr[n - i + 1][j] == 0)++zeor;
                else ++one;
                if(tr[i][m - j + 1] == 0)++zeor;
                else ++one;
                if(tr[n - i + 1][m - j + 1] == 0)++zeor;
                else ++one;
                ans += min(one, zeor);
            }
        }
        int num = 0;
        int num_one = 0;

        if(n % 2){
            for(int i = 1; i <= m / 2; ++i){
                if(tr[n / 2 + 1][i] != tr[n / 2 + 1][m - i + 1]){
                    ++ans;
                    ++num;
                }
                else{
                    if(tr[n / 2 + 1][i] == 1)++num_one;
                }
            }
        }
        if(m % 2){
            for(int i = 1; i <= n / 2; ++i){
                if(tr[i][m / 2 + 1] != tr[n - i + 1][m / 2 + 1]){
                    ++ans;
                    ++num;
                }
                else{
                    if(tr[i][m / 2 + 1] == 1)++num_one;
                }
            }
        }

        if(n % 2 == 0 && m % 2 == 0)return ans;
        else{
            if(n % 2 == 1 && m % 2 == 1){
                if(tr[n / 2 + 1][m / 2 + 1] == 1)++ans;
            }
            num_one %= 2;
            if(num < num_one)ans += 2;
            return ans;
        }
        return ans;
    }
};
